def clear():
    os.system("cls" if os.name == "nt" else "clear")

def show_banner():
    clear()
    print(bunny_ascii_art)
    print(Fore.YELLOW + Style.BRIGHT + "â˜… LUCID SCANNER â˜…")
    print(Fore.CYAN + "Website Recon Tool â€” Created by Darius")
    print(Fore.LIGHTBLACK_EX + f"Data: {datetime.datetime.now().strftime('%Y-%m-%d %H:%M')}")
    print(Fore.YELLOW + "-"*40)
    print(Fore.BLUE + "Inicializando mÃ³dulos...\n")

def whois_lookup(domain):
    try:
        w = whois.whois(domain)
        print(Fore.GREEN + "[+] WHOIS Info:")
        for key, value in w.items():
            print(Fore.LIGHTCYAN_EX + f"{key}: {value}")
    except Exception as e:
        print(Fore.RED + f"[-] Erro no WHOIS: {e}")

def ip_lookup(domain):
    try:
        ip = socket.gethostbyname(domain)
        print(Fore.GREEN + f"[+] IP do domÃ­nio {domain}: {ip}")
    except Exception as e:
        print(Fore.RED + f"[-] Erro ao buscar IP: {e}")

def dns_lookup(domain):
    try:
        print(Fore.GREEN + f"[+] Registros DNS para {domain}:")
        for qtype in ['A', 'MX', 'NS', 'TXT']:
            try:
                answers = dns.resolver.resolve(domain, qtype)
                for rdata in answers:
                    print(Fore.LIGHTCYAN_EX + f"{qtype} -> {rdata}")
            except:
                pass
    except Exception as e:
        print(Fore.RED + f"[-] Erro ao buscar DNS: {e}")

def menu():
    while True:
        print(Fore.YELLOW + "\nMENU:")
        print("1. WHOIS Lookup")
        print("2. IP Lookup")
        print("3. DNS Lookup")
        print("4. Sair")
        escolha = input(Fore.CYAN + "\nEscolha uma opÃ§Ã£o: ")

        if escolha == "1":
            domain = input(Fore.BLUE + "Digite o domÃ­nio: ")
            whois_lookup(domain)
        elif escolha == "2":
            domain = input(Fore.BLUE + "Digite o domÃ­nio: ")
            ip_lookup(domain)
        elif escolha == "3":
            domain = input(Fore.BLUE + "Digite o domÃ­nio: ")
            dns_lookup(domain)
        elif escolha == "4":
            print(Fore.RED + "\nSaindo da Lucid...")
            break
        else:
            print(Fore.RED + "OpÃ§Ã£o invÃ¡lida!")

if __name__ == "__main__":
    show_banner()
    menu()
